pageContext.rootQuery:
{
    prismic {
        allProjects (sortBy: project_post_date_DESC) {
            edges {
                node {
                    project_title
                    project_preview_description
                    project_preview_thumbnail
                    project_category
                    project_post_date
                    _meta {
                        uid
                    }
                }
            }
        }
    }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
